package runnable;

public class Hello {

	public static void main(String[] args) {
		/* TODO Auto-generated method stub */
		System.out.println("Hello World");
		System.out.println("I am Learning Java.");
		System.out.println("It is awesome!");
		System.out.println("This sentence will work");
		//System.out.println(This sentence will produce error); It misses double quotation inside the bracket
		/* The print() Method - differs from println() because the print() allow us to print the output in the same line*/
		System.out.print("Hello world");
		System.out.print(" " + "I will print on the same line");
	// println() and print() can be used to print numbers
		System.out.println();
		System.out.println(3);
		System.out.println();
		System.out.print(2);
		System.out.print(" "+ 3);
		System.out.println(" "+ 789);
		// Mathematical calculations can also be done inside println()
		System.out.println(3+3);
		System.out.println(3*3);
		System.out.println(4/3);
		// two ways to add comment - /* */(Multi Line comment) or //(single line comment)
	}

}
